import React from 'react';
import './ItemsSidebar.css';

const ItemsSidebar = ({ items, onSelectItem, selectedItemId, loading }) => {
  if (loading) {
    return (
      <aside className="items-sidebar">
        <div className="items-header">
          <h3>Items</h3>
        </div>
        <div className="items-loading">Loading items...</div>
      </aside>
    );
  }

  return (
    <aside className="items-sidebar">
      <div className="items-header">
        <h3>Items ({items.length})</h3>
      </div>
      <div className="items-list">
        {items.length === 0 ? (
          <div className="no-items">No items yet. Create your first item!</div>
        ) : (
          items.map((item) => (
            <div
              key={item.id}
              className={`item-card ${selectedItemId === item.id ? 'item-card-selected' : ''}`}
              onClick={() => onSelectItem(item)}
            >
              <div className="item-header">
                <h4 className="item-title">{item.name}</h4>
                <span className="item-id">#{item.id}</span>
              </div>
              {item.description && (
                <p className="item-description">{item.description}</p>
              )}
              <div className="item-meta">
                <span className="item-date">{new Date(item.created_at).toLocaleDateString()}</span>
              </div>
            </div>
          ))
        )}
      </div>
    </aside>
  );
};

export default ItemsSidebar;
