import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/Header';
import ItemsSidebar from './components/ItemsSidebar';
import ItemDetails from './components/ItemDetails';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showNewForm, setShowNewForm] = useState(false);

  // Fetch items on component mount
  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/items');
      setItems(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch items: ' + (err.response?.data?.detail || err.message));
    } finally {
      setLoading(false);
    }
  };

  const createItem = async (itemData) => {
    try {
      const response = await axios.post('/api/items', itemData);
      const newItem = response.data;
      setItems([newItem, ...items]);
      setSelectedItem(newItem); // Select the newly created item
      setShowNewForm(false); // Hide the form
      setSuccess('Item created successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
      return newItem;
    } catch (err) {
      setError('Failed to create item: ' + (err.response?.data?.detail || err.message));
      throw err;
    }
  };

  const deleteItem = async (itemId) => {
    try {
      await axios.delete(`/api/items/${itemId}`);
      setItems(items.filter(item => item.id !== itemId));
      // If we deleted the selected item, clear selection
      if (selectedItem && selectedItem.id === itemId) {
        setSelectedItem(null);
      }
      setSuccess('Item deleted successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to delete item: ' + (err.response?.data?.detail || err.message));
    }
  };

  const handleNewItem = () => {
    setShowNewForm(true);
    setSelectedItem(null);
  };

  const handleSelectItem = (item) => {
    setSelectedItem(item);
    setShowNewForm(false);
  };

  const handleCancelForm = () => {
    setShowNewForm(false);
    setError(null);
    setSuccess(null);
  };

  return (
    <div className="App">
      <Header onNewItem={handleNewItem} />
      <ItemsSidebar 
        items={items} 
        onSelectItem={handleSelectItem}
        selectedItemId={selectedItem?.id}
        loading={loading}
      />
      <main className="main-content">
        <ItemDetails
          selectedItem={selectedItem}
          showNewForm={showNewForm}
          onCreateItem={createItem}
          onDeleteItem={deleteItem}
          onCancelForm={handleCancelForm}
          error={error}
          success={success}
        />
      </main>
    </div>
  );
}

export default App;
