import React, { useState, useEffect, useRef, useCallback } from 'react';

const PromptForm = ({ onSubmit, initialData = null, projects = [], selectedProjectId = null, onAutoSave = null }) => {
  const [formData, setFormData] = useState({
    name: initialData?.name || '',
    status: initialData?.status || 'draft',
    content: initialData?.content || '',
    project_id: initialData?.project_id || selectedProjectId || ''
  });
  const [submitting, setSubmitting] = useState(false);
  const [autoSaving, setAutoSaving] = useState(false);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const autoSaveTimeoutRef = useRef(null);
  const initialDataRef = useRef(initialData);

  // Update form data when initialData changes (when switching between prompts)
  useEffect(() => {
    if (initialData) {
      setFormData({
        name: initialData.name || '',
        status: initialData.status || 'draft',
        content: initialData.content || '',
        project_id: initialData.project_id || ''
      });
    } else {
      // Reset form for new prompt creation
      setFormData({
        name: '',
        status: 'draft',
        content: '',
        project_id: selectedProjectId || ''
      });
    }
  }, [initialData, selectedProjectId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      await onSubmit(formData);
      // Reset form after successful submission
      if (!initialData) {
        setFormData({
          name: '',
          status: 'draft',
          content: '',
          project_id: selectedProjectId || ''
        });
      }
    } catch (error) {
      console.error('Form submission error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="prompt-form">
      <div className="form-row">
        <div className="form-group">
          <label htmlFor="name">Prompt Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Enter prompt name"
            disabled={submitting}
          />
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            id="status"
            name="status"
            value={formData.status}
            onChange={handleChange}
            disabled={submitting}
          >
            <option value="draft">Draft</option>
            <option value="archived">Archive</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="project_id">Project</label>
          <select
            id="project_id"
            name="project_id"
            value={formData.project_id}
            onChange={handleChange}
            disabled={submitting}
          >
            <option value="">Select Project (optional)</option>
            {projects.map(project => (
              <option key={project.id} value={project.id}>
                {project.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="content">Content</label>
        <textarea
          id="content"
          name="content"
          value={formData.content}
          onChange={handleChange}
          rows="8"
          placeholder="Enter your prompt content..."
          disabled={submitting}
        />
      </div>

      <button 
        type="submit" 
        className="btn"
        disabled={submitting || !formData.name.trim()}
      >
        {submitting ? 'Creating...' : initialData ? 'Update Prompt' : 'Create Prompt'}
      </button>
    </form>
  );
};

export default PromptForm;
