import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import Header from './components/Header';
import PromptsSidebar from './components/PromptsSidebar';
import PromptDetails from './components/PromptDetails';
import './App.css';

function PromptApp() {
  const navigate = useNavigate();
  const params = useParams();
  const [prompts, setPrompts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedPrompt, setSelectedPrompt] = useState(null);
  const [showNewForm, setShowNewForm] = useState(false);

  // Fetch prompts on component mount
  useEffect(() => {
    fetchPrompts();
  }, []);

  const fetchPrompts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/prompts');
      setPrompts(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch prompts: ' + (err.response?.data?.detail || err.message));
    } finally {
      setLoading(false);
    }
  };

  const createPrompt = async (promptData) => {
    try {
      const response = await axios.post('/api/prompts', promptData);
      const newPrompt = response.data;
      setPrompts([newPrompt, ...prompts]);
      setSelectedPrompt(newPrompt); // Select the newly created prompt
      setShowNewForm(false); // Hide the form
      setSuccess('Prompt created successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
      return newPrompt;
    } catch (err) {
      setError('Failed to create prompt: ' + (err.response?.data?.detail || err.message));
      throw err;
    }
  };

  const deletePrompt = async (promptId) => {
    try {
      await axios.delete(`/api/prompts/${promptId}`);
      setPrompts(prompts.filter(prompt => prompt.id !== promptId));
      // If we deleted the selected prompt, clear selection
      if (selectedPrompt && selectedPrompt.id === promptId) {
        setSelectedPrompt(null);
      }
      setSuccess('Prompt deleted successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to delete prompt: ' + (err.response?.data?.detail || err.message));
    }
  };

  const handleNewPrompt = () => {
    setShowNewForm(true);
    setSelectedPrompt(null);
  };

  const handleSelectPrompt = (prompt) => {
    setSelectedPrompt(prompt);
    setShowNewForm(false);
  };

  const handleCancelForm = () => {
    setShowNewForm(false);
    setError(null);
    setSuccess(null);
  };

  return (
    <div className="App">
      <Header />
      <PromptsSidebar 
        prompts={prompts} 
        onSelectPrompt={handleSelectPrompt}
        selectedPromptId={selectedPrompt?.id}
        onNewPrompt={handleNewPrompt}
        loading={loading}
      />
      <main className="main-content">
        <PromptDetails
          selectedPrompt={selectedPrompt}
          showNewForm={showNewForm}
          onCreatePrompt={createPrompt}
          onDeletePrompt={deletePrompt}
          onCancelForm={handleCancelForm}
          error={error}
          success={success}
        />
      </main>
    </div>
  );
}

export default App;
