import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ItemList from './components/ItemList';
import ItemForm from './components/ItemForm';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Fetch items on component mount
  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/items');
      setItems(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch items: ' + (err.response?.data?.detail || err.message));
    } finally {
      setLoading(false);
    }
  };

  const createItem = async (itemData) => {
    try {
      const response = await axios.post('/api/items', itemData);
      setItems([response.data, ...items]);
      setSuccess('Item created successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
      return response.data;
    } catch (err) {
      setError('Failed to create item: ' + (err.response?.data?.detail || err.message));
      throw err;
    }
  };

  const deleteItem = async (itemId) => {
    try {
      await axios.delete(`/api/items/${itemId}`);
      setItems(items.filter(item => item.id !== itemId));
      setSuccess('Item deleted successfully!');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to delete item: ' + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <h1>Inkwell</h1>
          <p>A local React website with FastAPI backend</p>
        </div>

        {error && <div className="error">{error}</div>}
        {success && <div className="success">{success}</div>}

        <div className="grid">
          <div className="card">
            <h2>Create New Item</h2>
            <ItemForm onSubmit={createItem} />
          </div>

          <div className="card">
            <h2>Items ({items.length})</h2>
            {loading ? (
              <div className="loading">Loading items...</div>
            ) : (
              <ItemList items={items} onDelete={deleteItem} />
            )}
          </div>
        </div>

        <div className="card">
          <h2>API Information</h2>
          <p>Backend API is running and serving data from SQLite database.</p>
          <p>You can also interact with the API directly:</p>
          <ul>
            <li><strong>GET /api/items</strong> - Get all items</li>
            <li><strong>POST /api/items</strong> - Create a new item</li>
            <li><strong>GET /api/items/&#123;id&#125;</strong> - Get specific item</li>
            <li><strong>PUT /api/items/&#123;id&#125;</strong> - Update item</li>
            <li><strong>DELETE /api/items/&#123;id&#125;</strong> - Delete item</li>
            <li><strong>GET /api/health</strong> - Health check</li>
          </ul>
          <p>
            <a href="/docs" target="_blank" rel="noopener noreferrer">
              View API Documentation
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
